result:
query($id: ID!, $page:Int) {
  category(id: $id) {
    title
    path
    belongsTo(perPage: 6, page: $page) @paginate {
      totalCount
      pageInfo {
        totalPages
        currentPage
      }
      edges {
        node {
          ... on Blog {
            id
            title
            image(width: 800)
            path
            timeToRead
            featured
            humanTime: created(format: "DD MMM YYYY")
            datetime: created
            category {
              id
              title
              path
            }
            author {
              id
              name
              image(width: 64, height: 64, fit: inside)
              path
            }
          }
        }
      }
    }
  }  
}
