query($recordId: ID!, $tags: [ID]) {
  blog(id: $recordId) {
    title
    path
    image(width:1600, height:800)
    image_caption
    excerpt
    content
    humanTime : created(format:"DD MMMM YYYY")
    datetime : created(format:"ddd MMM DD YYYY hh:mm:ss zZ")

    timeToRead
    tags {
      id
      title
      path
    }
    category {
      id
      title
      path
      belongsTo(limit:4) {
        totalCount
        edges {
          node {
            ... on Blog {
              title
              path
            }
          }
        }
      }
    }
    author {
      id
      name
      image
      path
      bio
    }
  }

  related: allBlog(
    filter: { id: { ne: $recordId }, tags: {containsAny: $tags} }
  ) {
    edges {
      node {
        title
    path
    image(width:1600, height:800)
